import React from 'react';
import './App.css';
import { fetchWeatherByCoords, searchCities, fetchAirQualityByCoords } from './api';
import lottie from 'lottie-web';
import rainyMascotAnimation from './animations/rainy-mascot.json';
import sunnyMascotAnimation from './animations/sunny-mascot.json';
import coldMascotAnimation from './animations/cold-mascot.json';
import mildMascotAnimation from './animations/mild-mascot.json';

function Card({ children }) {
  return (
    <div className="card">
      {children}
    </div>
  );
}

function Header({ title }) {
  return (
    <div className="header">
      <button className="icon-btn" aria-label="Menu">≡</button>
      <div className="location">{title || 'Search a city'} ∘</div>
      <div style={{ width: 28 }} />
    </div>
  );
}

function WeatherMascot({ mode }) {
  // Use custom Lottie animations for all weather modes
  console.log('WeatherMascot rendered with mode:', mode);
  const containerRef = React.useRef(null);
  const [useLottie, setUseLottie] = React.useState(false);
  const animationRef = React.useRef(null);

  React.useEffect(() => {
    if (!containerRef.current) return;

    // Clean up previous animation
    if (animationRef.current) {
      animationRef.current.destroy();
      animationRef.current = null;
    }

    // Map weather modes to custom animations
    const animationMap = {
      rain: rainyMascotAnimation,
      hot: sunnyMascotAnimation,
      cold: coldMascotAnimation,
      mild: mildMascotAnimation
    };

    const animationData = animationMap[mode];

    if (animationData) {
      try {
        console.log('Loading Lottie animation for mode:', mode, 'with data:', animationData);
        animationRef.current = lottie.loadAnimation({
          container: containerRef.current,
          renderer: 'svg',
          loop: true,
          autoplay: true,
          animationData: animationData
        });
        console.log('Lottie animation loaded successfully');
        setUseLottie(true);
      } catch (error) {
        console.error('Failed to load custom Lottie animation:', error);
        setUseLottie(false);
      }
    } else {
      console.log('No animation data found for mode:', mode);
      setUseLottie(false);
    }

    return () => {
      if (animationRef.current) {
        try {
          animationRef.current.destroy();
        } catch (e) {}
        animationRef.current = null;
      }
    };
  }, [mode]);

  if (useLottie) return (
    <div className="mascot-stage enter">
      <div className="lottie-wrap" ref={containerRef} />
    </div>
  );

  const rainDrops = Array.from({ length: 12 });
  const snows = Array.from({ length: 10 });
  return (
    <div className="mascot-stage enter">
      <div className={`mascot ${mode} walk breath`}>
      <svg viewBox="0 0 120 160" className="float" aria-hidden>
        <g className="sway">
          <g>
            <circle className="skin" cx="60" cy="34" r="16" />
              <rect className="coat torso" x="40" y="56" width="40" height="52" rx="10" />
              <rect className="shirt torso" x="40" y="56" width="40" height="52" rx="10" />
            <g className="leg-left">
              <rect className="pants" x="46" y="108" width="12" height="28" rx="4" />
              <rect className="shoe" x="44" y="134" width="16" height="8" rx="3" />
            </g>
            <g className="leg-right">
              <rect className="pants" x="62" y="108" width="12" height="28" rx="4" />
              <rect className="shoe" x="60" y="134" width="16" height="8" rx="3" />
            </g>
            <rect className="arm" x="36" y="62" width="8" height="26" rx="4" fill="#ffe4cf" />
            <path className="scarf" d="M44 56h32v8H44z" />
            <path className="umbrella" d="M20 54c12-12 48-12 60 0h-60z" fill="#4a5568" />
            <circle className="sunhat" cx="60" cy="20" r="10" fill="#f6ad55" />
          </g>
        </g>
      </svg>
      <div className="particles">
        {mode === 'rain' && rainDrops.map((_, i) => (
          <span key={i} className="raindrop" style={{ left: 10 + (i*10%120), animationDelay: `${(i%6)*0.2}s` }} />
        ))}
        {mode === 'cold' && snows.map((_, i) => (
          <span key={i} className="snow" style={{ left: 10 + (i*10%120), animationDelay: `${(i%5)*0.3}s` }} />
        ))}
      </div>
        <div className="shadow" />
        {mode === 'hot' && <div className="sun-rays" />}
      </div>
    </div>
  );
}

function CurrentWeather({ data }) {
  const temp = Math.round(data?.current?.temp ?? 31);
  const feels = Math.round(data?.current?.feelsLike ?? 37);
  const min = Math.round(data?.daily?.[0]?.min ?? 25);
  const max = Math.round(data?.daily?.[0]?.max ?? 32);
  const mode = (() => {
    const t = temp;
    const humidity = data?.current?.humidity ?? 0;
    if (humidity > 70 && (data?.hourly?.[0]?.pop ?? 0) > 50) return 'rain';
    if (t <= 18) return 'cold';
    if (t >= 34) return 'hot';
    return 'mild';
  })();
  return (
    <div className="current">
      <div className="temp">{temp}°</div>
      <div className="desc">{' '}
        {data ? 'Updated' : 'Fog'}
      </div>
      <div className="range">{max}° / {min}° Feels like {feels}°</div>
      <WeatherMascot mode={mode} />
    </div>
  );
}

function HourlyCard({ data }) {
  return (
    <Card>
      <div className="card-title">Thunderstorms. Highs 31 to 33°C and lows 24 to 26°C.</div>
      <div className="hourly">
        {(data?.hourly ?? Array.from({ length: 7 })).map((h, i) => {
          const date = h?.time ? new Date(h.time) : new Date();
          const hour12 = (date.getHours() % 12) || 12;
          const mer = date.getHours() < 12 ? 'am' : 'pm';
          const temp = Math.round(h?.temp ?? 30 + (i % 3));
          const pop = h?.pop ?? [11,51,76,86,44,17,10][i] ?? 10;
          return (
            <div className="hour" key={i}>
              <div className="hour-time">{hour12}:30 {mer}</div>
              <div className="hour-icon">⛈️</div>
              <div className="hour-temp">{temp}°</div>
              <div className="hour-pop">{pop}%</div>
            </div>
          );
        })}
      </div>
    </Card>
  );
}

function RainCard() {
  return (
    <Card>
      <div className="rain-chart">
        {Array.from({ length: 7 }).map((_, i) => (
          <div className="bar" key={i} style={{ height: 12 + (i%4)*14 }} />
        ))}
      </div>
      <div className="rain-scale">
        {['0.0','0.32','1.43','1.51','0.01','0.0','0.0'].map((v, i) => (
          <div className="tick" key={i}>{v}</div>
        ))}
      </div>
    </Card>
  );
}

function AlertCard() {
  const slides = [
    { title: 'Grab an Umbrella!', sub: 'Thunderstorms possible after 14:00', qty: '3mm' },
    { title: 'Heat Alert', sub: 'High UV today between 11:00–15:00', qty: 'UV 9' },
    { title: 'Air Quality', sub: 'AQI is Moderate in your area', qty: 'AQI 35' },
    { title: 'Windy Evening', sub: 'Gusts up to 30 km/h after 18:00', qty: '30 km/h' }
  ];
  const [idx, setIdx] = React.useState(0);
  const ref = React.useRef(null);

  React.useEffect(() => {
    const id = setInterval(() => setIdx(i => (i + 1) % slides.length), 3500);
    return () => clearInterval(id);
  }, [slides.length]);

  React.useEffect(() => {
    if (ref.current) ref.current.style.transform = `translateX(-${idx * 100}%)`;
  }, [idx]);

  return (
    <Card>
      <div className="carousel" onMouseEnter={() => clearInterval()}>
        <div className="slides" ref={ref}>
          {slides.map((s, i) => (
            <div className="slide" key={i}>
              <div className="alert">
                <div>
                  <div className="alert-title">{s.title}</div>
                  <div className="alert-sub">{s.sub}</div>
                </div>
                <div className="alert-amount">{s.qty}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div className="dots">
        {slides.map((_, i) => (
          <span key={i} className={`dot ${i===idx?'active':''}`} onClick={() => setIdx(i)} />
        ))}
      </div>
    </Card>
  );
}

export default function App() {
  const [data, setData] = React.useState(null);
  const [error, setError] = React.useState(null);
  const [loading, setLoading] = React.useState(true);
  const [query, setQuery] = React.useState('');
  const [suggestions, setSuggestions] = React.useState([]);
  const [title, setTitle] = React.useState('Mevalurkuppam');
  const [unit, setUnit] = React.useState('C'); // 'C' | 'F'
  const [aqi, setAqi] = React.useState(null);
  const [theme, setTheme] = React.useState('theme-mild');

  React.useEffect(() => {
    async function onPosition({ coords }) {
      try {
        const [w, a] = await Promise.all([
          fetchWeatherByCoords(coords.latitude, coords.longitude),
          fetchAirQualityByCoords(coords.latitude, coords.longitude)
        ]);
        setData(w); setAqi(a);
      } catch (e) { setError(e); }
      finally { setLoading(false); }
    }
    async function onError() {
      try {
        const [w, a] = await Promise.all([
          fetchWeatherByCoords(13.0827, 80.2707),
          fetchAirQualityByCoords(13.0827, 80.2707)
        ]);
        setData(w); setAqi(a);
      } catch (e) { setError(e); }
      finally { setLoading(false); }
    }
    if (navigator.geolocation) navigator.geolocation.getCurrentPosition(onPosition, onError);
    else onError();
  }, []);

  React.useEffect(() => {
    let active = true;
    const t = setTimeout(() => {
      searchCities(query).then(res => { if (active) setSuggestions(res); });
    }, 250);
    return () => { active = false; clearTimeout(t); };
  }, [query]);

  React.useEffect(() => {
    function onClick(e) {
      const container = document.querySelector('.search');
      if (container && !container.contains(e.target)) setSuggestions([]);
    }
    window.addEventListener('click', onClick);
    return () => window.removeEventListener('click', onClick);
  }, []);

  function onPick(city) {
    setTitle([city.name, city.admin1, city.country].filter(Boolean).join(', '));
    setSuggestions([]);
    setQuery('');
    setLoading(true);
    Promise.all([
      fetchWeatherByCoords(city.latitude, city.longitude),
      fetchAirQualityByCoords(city.latitude, city.longitude)
    ])
      .then(([w, a]) => { setData(w); setAqi(a); })
      .catch(setError)
      .finally(() => setLoading(false));
  }

  function toUnit(celsius) {
    if (celsius == null) return celsius;
    return unit === 'C' ? Math.round(celsius) : Math.round((celsius * 9) / 5 + 32);
  }

  React.useEffect(() => {
    const root = document.querySelector('.parallax');
    if (!root) return;
    function onMove(e) {
      const { innerWidth: w, innerHeight: h } = window;
      const x = (e.clientX / w - 0.5) * 2;
      const y = (e.clientY / h - 0.5) * 2;
      const layers = root.querySelectorAll('.layer');
      layers.forEach((el, i) => {
        const depth = i === 0 ? 10 : 20;
        el.style.transform = `translateZ(${-150 - i*100}px) translate(${x*depth}px, ${y*depth}px) scale(${1.2 + i*0.2})`;
      });
      document.documentElement.style.setProperty('--tiltX', `${x*2}deg`);
      document.documentElement.style.setProperty('--tiltY', `${-y*2}deg`);
    }
    window.addEventListener('mousemove', onMove);
    return () => window.removeEventListener('mousemove', onMove);
  }, []);

  // Gyro support (mobile)
  React.useEffect(() => {
    function handleOrientation(e) {
      const x = (e.gamma || 0) / 45; // left-right
      const y = (e.beta || 0) / 45;  // front-back
      document.documentElement.style.setProperty('--tiltX', `${x}deg`);
      document.documentElement.style.setProperty('--tiltY', `${-y}deg`);
    }
    window.addEventListener('deviceorientation', handleOrientation);
    return () => window.removeEventListener('deviceorientation', handleOrientation);
  }, []);

  // Theme from weather
  React.useEffect(() => {
    if (!data) return;
    const t = data.current?.temp ?? 26;
    const humidity = data.current?.humidity ?? 0;
    const pop = data.hourly?.[0]?.pop ?? 0;
    const newTheme = humidity > 70 && pop > 50 ? 'theme-rain' : t <= 18 ? 'theme-cold' : t >= 34 ? 'theme-hot' : 'theme-mild';
    setTheme(newTheme);
  }, [data]);

  return (
    <div className={`app ${theme}`}>
      <div className="gradient" />
      <div className="parallax" aria-hidden>
        <div className="layer one" />
        <div className="layer two stars" />
      </div>
      <Header title={title} />
      <div className="controls">
        <button className={`toggle ${unit==='C'?'active':''}`} onClick={() => setUnit('C')}>°C</button>
        <button className={`toggle ${unit==='F'?'active':''}`} onClick={() => setUnit('F')}>°F</button>
      </div>
      <div className="search">
        <input
          value={query}
          onChange={e => setQuery(e.target.value)}
          placeholder="Search city or town"
        />
        {suggestions.length > 0 && (
          <div className="suggestions">
            {suggestions.map(s => (
              <button key={s.id} onClick={() => onPick(s)}>
                {s.name}{s.admin1 ? `, ${s.admin1}` : ''}, {s.country}
              </button>
            ))}
          </div>
        )}
      </div>
      {error && <div className="card">Failed to load weather.</div>}
      <CurrentWeather data={data ? { ...data, current: { ...data.current, temp: toUnit(data.current?.temp), feelsLike: toUnit(data.current?.feelsLike) }, daily: data.daily?.map(d => ({...d, max: toUnit(d.max), min: toUnit(d.min)})) } : null} />
      <div className="stack">
        <HourlyCard data={data ? { ...data, hourly: data.hourly?.map(h => ({...h, temp: toUnit(h.temp)})) } : null} />
        <RainCard />
        <AlertCard />
        {data && (
          <div className="tiles">
            <div className="tile"><div className="k">AQI</div><div className="v">{aqi ? `${aqi.level} (${aqi.aqi})` : '—'}</div></div>
            <div className="tile"><div className="k">UV index</div><div className="v">{data.sun?.uvMax ?? '—'}</div></div>
            <div className="tile"><div className="k">Humidity</div><div className="v">{data.current?.humidity ?? '—'}%</div></div>
            <div className="tile"><div className="k">Wind</div><div className="v">{data.current?.windKmh ? `${Math.round(data.current.windKmh)} km/h` : '—'}</div></div>
            <div className="tile"><div className="k">Dew point</div><div className="v">{toUnit(data.current?.dewPoint)}°</div></div>
            <div className="tile"><div className="k">Pressure</div><div className="v">{Math.round(data.current?.pressureMb ?? 0)} mb</div></div>
            <div className="tile"><div className="k">Visibility</div><div className="v">{data.current?.visibilityKm ? `${data.current.visibilityKm.toFixed(2)} km` : '—'}</div></div>
          </div>
        )}
        {data?.sun?.sunrise && data?.sun?.sunset && (
          <Card>
            <div className="card-title">Sunrise & Sunset</div>
            <div className="sun-arc">
              <svg viewBox="0 0 100 50" preserveAspectRatio="none">
                <path d="M0,50 A50,50 0 0,1 100,50" fill="none" stroke="rgba(255,255,255,.4)" strokeWidth="2" />
              </svg>
              <div className="time-row">
                <div>{new Date(data.sun.sunrise).toLocaleTimeString([], { hour: 'numeric', minute: '2-digit' })}</div>
                <div>{new Date(data.sun.sunset).toLocaleTimeString([], { hour: 'numeric', minute: '2-digit' })}</div>
              </div>
            </div>
          </Card>
        )}
        {data && (
          <Card>
            <div className="card-title">Daily forecast</div>
            {(data.daily || []).slice(0,7).map((d, i) => (
              <div className="row" key={i}>
                <div className="label">{d.date ? new Date(d.date).toLocaleDateString(undefined, { weekday: 'long' }) : 'Day'}</div>
                <div className="icons">⛈️</div>
                <div className="hi-lo">{toUnit(d.max)}° {toUnit(d.min)}°</div>
              </div>
            ))}
          </Card>
        )}
      </div>
      {loading && <div style={{marginTop:12, opacity:.8}}>Loading…</div>}
    </div>
  );
}
